import React, { useState, useEffect } from "react";

const App = () => {
  const [person, setPerson] = useState({});

  useEffect(() => {
    const fetchPerson = () =>
      fetch("https://swapi.co/api/people/")
        .json()
        .then(res => setPerson(res))
        .then(data => setPerson(data));
    fetchPerson();
  }, [person]);

  return (
    <div>
      <span>{console.log(person.data)}</span>
      <hr />
    </div>
  );
};
export default App;
